mapper.insert:

INSERT INTO EZ_BBS_COMMENT
(
	CM_IDX
	, CB_IDX
	, BC_IDX
	, UPR_CM_IDX
	, COMMENT_CONT
	, REG_NAME
	, REG_ID
	, REG_DT
	, REG_IP
	, CM_DEPTH
)
VALUES
(
	(SELECT NVL(MAX(CM_IDX),0)+1 FROM EZ_BBS_COMMENT)
	, #{cbIdx}
	, #{bcIdx}
	, #{uprCmIdx}
	, #{cmCont}
	, #{regName}
	, #{regId}
	, SYSDATE
	, #{regIp}
	, #{cmDepth}
)

mapper.update:

UPDATE EZ_BBS_COMMENT SET
	CM_DEL_YN='Y'
WHERE CM_IDX=#{cmIdx}


UPDATE EZ_BBS_COMMENT SET
	COMMENT_CONT = #{cmCont}
	, MOD_ID=#{modId}
	, MOD_DT=SYSDATE
	, MOD_IP=#{modIp}
WHERE CM_IDX=#{cmIdx}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, CM.* FROM (SELECT CM_IDX, BC_IDX, CASE WHEN CM_DEL_YN = 'Y' THEN '삭제된 댓글입니다.' ELSE COMMENT_CONT END AS COMMENT_CONT, TO_CHAR(REG_DT, 'YYYY-MM-DD') AS REG_DT, REG_NAME, REG_ID, CM_DEPTH, CM_DEL_YN FROM EZ_BBS_COMMENT A WHERE (CM_DEL_YN='N' OR (SELECT COUNT(*) FROM EZ_BBS_COMMENT where CM_DEL_YN='N'START WITH UPR_CM_IDX = A.CM_IDX CONNECT BY NOCYCLE PRIOR CM_IDX = UPR_CM_IDX) > 0)
and BC_IDX = #{bcIdx}
and CB_IDX = #{cbIdx}
START WITH UPR_CM_IDX = '0' CONNECT BY NOCYCLE PRIOR CM_IDX = UPR_CM_IDX ORDER SIBLINGS BY CM_IDX DESC
) CM ) WHERE RN BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}


SELECT COUNT(*) FROM EZ_BBS_COMMENT A
where (CM_DEL_YN='N' OR (SELECT COUNT(*) FROM EZ_BBS_COMMENT  where CM_DEL_YN='N'START WITH UPR_CM_IDX = A.CM_IDX CONNECT BY NOCYCLE PRIOR CM_IDX = UPR_CM_IDX) > 0)



and BC_IDX = #{bcIdx}
