	, PQ_TYPE
	, PQ_ITEM_CNT
	, PQ_ITEM_LIST
	, PQ_ITEM_DIRECTION
	, PQ_CHECK
	, PQ_ETC
	, REG_ID
)
VALUES
(
	  #{pqIdx}
	, #{pqPlidx}
	, #{pqSort}
	, #{pqTitle}
	, #{pqDetail}
	, #{pqType}
	, #{pqItemCnt}
	, #{pqItemList}
	, #{pqItemDirection}
	, #{pqCheck}
	, #{pqEtc}
	, #{regId}
)

mapper.update:

UPDATE EZ_POLL_QUESTION 
      SET PQ_TITLE = #{pqTitle}
	, PQ_DETAIL = #{pqDetail}
	, PQ_TYPE = #{pqType}
	, PQ_ITEM_CNT = #{pqItemCnt}
	, PQ_ITEM_LIST = #{pqItemList}
	, PQ_ITEM_DIRECTION = #{pqItemDirection}
	, PQ_CHECK = #{pqCheck}
	, PQ_ETC = #{pqEtc}
	, MOD_DT = NOW()
	, MOD_ID = #{modId}
WHERE PQ_IDX = #{pqIdx}


UPDATE EZ_POLL_QUESTION
SET 
	  USE_YN = 'N'
	, MOD_DT = NOW()
	, MOD_ID = #{modId}
WHERE PQ_IDX = #{pqIdx}

mapper.select:

SELECT * FROM EZ_POLL_QUESTION
WHERE USE_YN = 'Y'
AND PQ_IDX = #{pqIdx}

mapper.update:

UPDATE EZ_POLL_QUESTION 
SET PQ_SORT = #{pqSort}
WHERE PQ_IDX = #{pqIdx}


UPDATE EZ_POLL_QUESTION 
SET PQ_SORT = #{changeSort}
WHERE USE_YN = 'Y'
AND PQ_PLIDX = #{pqPlidx}
AND PQ_SORT = #{pqSort}


UPDATE EZ_POLL_QUESTION 
SET PQ_SORT = PQ_SORT - 1
WHERE USE_YN = 'Y'
AND PQ_PLIDX = #{pqPlidx}
AND PQ_SORT > #{pqSort}
