SELECT * FROM ( SELECT /*+ FIRST_ROWS */ A.*, ROWNUM RN FROM ( SELECT RI_IDX, RI_TITLE, RI_SUPERVISION_ORG, RI_SUPERVISION_ORG_NAME, DECODE(RI_LOT_USE, 'OL', '온라인추첨', 'OO', '온라인 선착순', 'OF', '기타 접수') RI_LOT_USE, DECODE(RI_CONFIRM_YN, 'Y', '승인', 'N', '미승인') RI_CONFIRM_YN, CASE WHEN RI_RESERVATION_SDATE||' '||LPAD(RI_RESERVATION_SDATE_HH, 2, '0')||':'||LPAD(RI_RESERVATION_SDATE_MM, 2, '0') >= TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi') THEN '예약대기' WHEN RI_RESERVATION_SDATE||' '||LPAD(RI_RESERVATION_SDATE_HH, 2, '0')||':'||LPAD(RI_RESERVATION_SDATE_MM, 2, '0') <= TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi') AND TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi') <= RI_RESERVATION_EDATE||' '||LPAD(RI_RESERVATION_EDATE_HH, 2, '0')||':'||LPAD(RI_RESERVATION_EDATE_MM, 2, '0') THEN '예약중' /* WHEN RI_RESERVATION_EDATE||' '||LPAD(RI_RESERVATION_EDATE_HH, 2, '0')||':'||LPAD(RI_RESERVATION_EDATE_MM, 2, '0') <= TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi') AND RI_CONFIRM_SDATE||' '||LPAD(RI_CONFIRM_SDATE_HH, 2, '0')||':'||LPAD(RI_CONFIRM_SDATE_MM, 2, '0') >= TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi') THEN '예약완료' WHEN RI_CONFIRM_SDATE||' '||LPAD(RI_CONFIRM_SDATE_HH, 2, '0')||':'||LPAD(RI_CONFIRM_SDATE_MM, 2, '0') >= TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi') AND RI_CONFIRM_EDATE||' '||LPAD(RI_CONFIRM_EDATE_HH, 2, '0')||':'||LPAD(RI_CONFIRM_SDATE_MM, 2, '0') >= TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi') THEN '당첨발표' WHEN RI_SDATE <= TO_CHAR(SYSDATE, 'yyyy-mm-dd') AND RI_EDATE >= TO_CHAR(SYSDATE, 'yyyy-mm-dd') THEN '행사중' WHEN RI_SDATE <= TO_CHAR(SYSDATE, 'yyyy-mm-dd') AND RI_EDATE <= TO_CHAR(SYSDATE, 'yyyy-mm-dd') THEN '행사마감' WHEN RI_SDATE <= TO_CHAR(SYSDATE, 'yyyy-mm-dd') AND RI_EDATE is null THEN '행사마감' */ ELSE '예약마감' END RI_PROGRESS, RI_SDATE, RI_EDATE, RI_RESERVATION_SDATE, RI_RESERVATION_EDATE, RI_IMAGE_FILE_ID, (SELECT CD_SUBJECT FROM EZ_DEPARTMENT WHERE CD_IDX = ri.RI_MANAGE_DEPT) RI_MANAGE_DEPT_TXT, RI_READ_CNT, RI_PLACE, RI_RECRUIT_YN, RI_RECRUIT_CNT, RI_RESERVATION_SDATE||' '||LPAD(RI_RESERVATION_SDATE_HH, 2, '0')||':'||LPAD(RI_RESERVATION_SDATE_MM, 2, '0') RI_RESERVATION_SDT, RI_RESERVATION_EDATE||' '||LPAD(RI_RESERVATION_EDATE_HH, 2, '0')||':'||LPAD(RI_RESERVATION_EDATE_MM, 2, '0') RI_RESERVATION_EDT, (select count(RI_IDX) from reservation_user_info where ri_idx = ri.ri_idx and ru_del_yn = 'N') as USERCNT, (select STRE_FILE_NM from COMTNFILEDETAIL where ATCH_FILE_ID = ri.RI_IMAGE_FILE_ID) as RI_MAIN_FILE_NM, RI_OFF_LOT_USE FROM EZ_RESERVATION_INDEX ri WHERE USE_YN = 'Y' AND RI_TYPE = #{riType}
ORDER BY REG_DT DESC
ORDER BY RI_READ_CNT DESC
ORDER BY RI_CONFIRM_SDATE DESC
ORDER BY REG_DT DESC
) A WHERE ROWNUM <= #{lastIndex} ) WHERE RN > #{firstIndex}

SELECT B.RI_IDX,B.RI_TITLE,B.RI_SUPERVISION_ORG,B.RI_SUPERVISION_ORG_NAME,B.RI_LOT_USE,B.RI_PROGRESS, RI_SDATE,RI_EDATE,RI_IMAGE_FILE_ID,RI_PLACE,RI_MAIN_FILE_NM,RI_OFF_LOT_USE,B.REG_DT,B.RI_EVENT_NUM,rt.RA_TITLE,rt.RA_CONTENT FROM(SELECT * FROM ( SELECT /*+ FIRST_ROWS */ A.*, ROWNUM RN FROM ( SELECT RI_IDX, RI_TITLE, RI_SUPERVISION_ORG, RI_SUPERVISION_ORG_NAME, DECODE(RI_LOT_USE, 'OL', '온라인추첨', 'OO', '온라인 선착순', 'OF', '기타 접수') RI_LOT_USE, DECODE(RI_CONFIRM_YN, 'Y', '승인', 'N', '미승인') RI_CONFIRM_YN, CASE WHEN RI_SDATE >= TO_CHAR(SYSDATE, 'yyyy-mm-dd') THEN 'ON' WHEN RI_SDATE < TO_CHAR(SYSDATE, 'yyyy-mm-dd') AND RI_EDATE < TO_CHAR(SYSDATE, 'yyyy-mm-dd') THEN 'OFF' WHEN RI_SDATE < TO_CHAR(SYSDATE, 'yyyy-mm-dd') AND RI_EDATE is null THEN 'OFF' ELSE '' END RI_PROGRESS, RI_SDATE, RI_EDATE, RI_IMAGE_FILE_ID, RI_PLACE, (select STRE_FILE_NM from COMTNFILEDETAIL where ATCH_FILE_ID = ri.RI_IMAGE_FILE_ID) as RI_MAIN_FILE_NM, RI_OFF_LOT_USE, REG_DT, RI_EVENT_NUM FROM EZ_RESERVATION_INDEX ri WHERE USE_YN = 'Y' AND RI_TYPE = #{riType} AND RI_EVENT_TYPE = #{riEventType} AND RI_SUPERVISION_ORG = #{riSupervisionOrg}
AND RI_TITLE LIKE CONCAT('%', #riTitle ,'%')
ORDER BY REG_DT DESC ) A WHERE ROWNUM <= #{lastIndex} ) WHERE RN > #{firstIndex})B, RESERVATION_ADD_ITEM rt WHERE B.RI_IDX = rt.RI_IDX ORDER BY REG_DT DESC, RA_IDX


