mapper.insert:

DECLARE 
			BEGIN

			UPDATE EZ_HOT_LIST
			SET HL_SORT = HL_SORT + 1
			WHERE HL_SORT > 0;

			INSERT INTO
			HOT_LIST(
				HL_IDX
				,HL_TITLE
				,HL_LINK
				,HL_TARGET
				,HL_SORT
				,HL_FILE_NAME
				,HL_FILE_ORG_NAME
				,HL_FILE_PATH
				,USE_YN
				,REG_DT
				,REG_ID
				,HL_MO_FILE_NAME
				,HL_MO_FILE_ORG_NAME
				,HL_MO_FILE_PATH
				)VALUES(
				#{hlIdx}
				,#{hlTitle}
				,#{hlLink}
				,#{hlTarget}



			 		,1




			 		,0



				,#{hlFileName}
				,#{hlFileOrgName}
				,#{hlFilePath}
				,#{useYn}
				,DATE_FORMAT(NOW(),'%Y/%m/%d')
				,#{regId}
				,#{hlMoFileName}
				,#{hlMoFileOrgName}
				,#{hlMoFilePath}
				);

		END;

mapper.update:

UPDATE EZ_HOT_LIST
SET HL_TITLE = #{hlTitle}
	,HL_LINK = #{hlLink}
	,HL_TARGET = #{hlTarget}




	 		,HL_SORT = (SELECT NVL(MAX(HL_SORT)+1,1) FROM EZ_HOT_LIST)





 		,HL_SORT = 0



	,HL_FILE_NAME = #{hlFileName}
	,HL_FILE_ORG_NAME = #{hlFileOrgName}
	,HL_FILE_PATH = #{hlFilePath}
	,USE_YN = #{useYn}
	,MOD_DT = DATE_FORMAT(NOW(), %Y-%m-%d)
	,MOD_ID = #{modId}
	,HL_MO_FILE_NAME = #{hlMoFileName}
	,HL_MO_FILE_ORG_NAME = #{hlMoFileOrgName}
	,HL_MO_FILE_PATH = #{hlMoFilePath}
WHERE HL_IDX= #{hlIdx}


UPDATE EZ_HOT_LIST
SET HL_SORT = HL_SORT - 1
WHERE HL_SORT > #{hlSort}


DECLARE 
		BEGIN

		UPDATE EZ_HOT_LIST
		SET HL_SORT = #{hlTempSort}
		WHERE HL_IDX = #{hlIdx};

		UPDATE EZ_HOT_LIST
		SET HL_SORT = #{hlSort}
		WHERE HL_IDX = #{hlTempIdx};

END;


UPDATE EZ_HOT_LIST
SET USE_YN = 'N'
WHERE HL_IDX = #{hlIdx}
