mapper.insert:

INSERT INTO EZ_KCM_PLEDGE_BANK_FILE
(
	PL_IDX
	, FILE_NO
	, FILE_STRE_COURS
	, STRE_FILE_NM
	, ORIGNL_FILE_NM
	, FILE_EXTSN
	, FILE_SIZE
	, REG_ID
)
VALUES
(
	#{plIdx}
	, (SELECT IFNULL(MAX(FILE_NO), 0)+1 FROM EZ_KCM_PLEDGE_BANK_FILE EPBF WHERE PL_IDX = #{plIdx})
	, #{fileStreCours}
	, #{streFileNm}
	, #{orignlFileNm}
	, #{fileExtsn}
	, #{fileSize}
	, #{regId}
)

mapper.update:

UPDATE EZ_KCM_PLEDGE_BANK_FILE
SET 
	USE_YN = 'N'
	, MOD_DT = NOW()
	, MOD_ID = #{modId}
WHERE PL_IDX = #{plIdx}
AND STRE_FILE_NM = #{streFileNm}
