mapper.update:

UPDATE EZ_KCM_PLEDGE_BANK
SET 
	PL_DEL_YN = 'Y'
	, MOD_DT = SYSDATE
	, MOD_ID = #{modId}
	, MOD_IP = #{modIp}
WHERE PL_IDX=#{plIdx}

mapper.select:
SELECT PL_IDX , SUB_CONT , TEL_CONT , EMAIL_CONT , REG_TYPE , REG_ID , REG_NAME , TO_CHAR(REG_DT, 'YYYY-MM-DD') AS REG_DT , COUNT_CONT , RECOMMEND_CONT , CATE_CONT_TXT , PL_WIW_TXT1 , PL_WIW_TXT2 , FILE_CNT FROM ( SELECT A.*, ROWNUM RN FROM ( SELECT PL_IDX , SUB_CONT , TEL_CONT , EMAIL_CONT , REG_TYPE , REG_ID , REG_NAME , REG_DT , COUNT_CONT , RECOMMEND_CONT , (SELECT CODE_NAME FROM EZ_CMM_CODE WHERE CODE=A.CATE_CONT) AS CATE_CONT_TXT , (SELECT WIWNAME FROM EZ_KCM_WIWON_MST WHERE WIWID=A.PL_WIWID1) AS PL_WIW_TXT1 , CASE WHEN A.PL_WIWID2!=0 THEN (SELECT WIWNAME FROM EZ_KCM_WIWON_MST WHERE WIWID=A.PL_WIWID2) ELSE '-' END AS PL_WIW_TXT2 , (SELECT COUNT(*) FROM EZ_KCM_PLEDGE_BANK_FILE WHERE PL_IDX=A.PL_IDX AND USE_YN='Y') AS FILE_CNT FROM EZ_KCM_PLEDGE_BANK A WHERE PL_DEL_YN='N'
ORDER BY REG_DT DESC
ORDER BY RECOMMEND_CONT DESC
ORDER BY COUNT_CONT DESC
) A WHERE ROWNUM <= #{lastIndex} ) B WHERE RN > #{firstIndex}
