mapper.insert:

INSERT INTO EZ_CMS_QUICK_LINK
(
	CQL_IDX
	, CQL_NAME
	, CQL_LINK
	, CQL_CODE
	, CQL_TARGET
	, CQL_SORT
	, CQL_HOT_YN
	, CQL_NEW_YN
	, REG_ID
)
VALUES
(
	#{cqlIdx}
	, #{cqlName}
	, #{cqlLink}
	, #{cqlCode}
	, #{cqlTarget}
	, #{cqlSort}
	, #{cqlHotYn}
	, #{cqlNewYn}
	, #{regId}
)

mapper.update:

UPDATE EZ_CMS_QUICK_LINK
SET CQL_NAME = #{cqlName}
	, CQL_LINK = #{cqlLink}
	, CQL_CODE = #{cqlCode}
	, CQL_TARGET = #{cqlTarget}



	, CQL_SORT = #{cqlSort}



	, CQL_HOT_YN = #{cqlHotYn}
	, CQL_NEW_YN = #{cqlNewYn}
	, CQL_USE = #{cqlUse}
	, MOD_DT = NOW()
	, MOD_ID = #{modId}
WHERE CQL_IDX= #{cqlIdx}


UPDATE EZ_CMS_QUICK_LINK
SET
	MOD_DT = NOW()
	, MOD_ID = #{modId}



	,CQL_HOT_YN = #{cqlHotYn}




	,CQL_NEW_YN = #{cqlNewYn}





	,CQL_USE ='Y'
	, CQL_SORT = (SELECT IFNULL(MAX(CQL_SORT), 0)+1 FROM EZ_CMS_QUICK_LINK WHERE USE_YN = 'Y' AND CQL_USE = 'Y' AND CQL_CODE = #{cqlCode})




	,CQL_USE = 'N'
	, CQL_SORT = 0




WHERE CQL_IDX= #{cqlIdx}


UPDATE EZ_CMS_QUICK_LINK
SET USE_YN = 'N'
	, MOD_DT = NOW()
	, MOD_ID = #{modId}
WHERE CQL_IDX = #{cqlIdx}

mapper.select:
SELECT * FROM ( SELECT /*+ FIRST_ROWS */ A.*, @rownum := @rownum + 1 as RN FROM ( SELECT CQL_IDX , CQL_NAME , CQL_LINK , CQL_CODE , (SELECT CODE_NAME FROM EZ_CMM_CODE WHERE CODE = CQL_CODE) AS CQL_CODE_NAME , CQL_TARGET , CQL_SORT , CQL_HOT_YN , CQL_NEW_YN , CQL_USE , USE_YN , REG_DT , REG_ID , MOD_DT , MOD_ID FROM EZ_CMS_QUICK_LINK WHERE USE_YN = 'Y' AND (@rownum:=0)=0
ORDER BY CQL_CODE ASC, CQL_SORT ASC, CQL_NAME ASC
ORDER BY CQL_CODE ASC, CQL_NAME ASC
ORDER BY CQL_CODE ASC, CQL_SORT ASC, CQL_NAME ASC
) A WHERE ROWNUM <= #{lastIndex} ) B WHERE RN > #{firstIndex}

mapper.update:

UPDATE EZ_CMS_QUICK_LINK 
SET CQL_SORT = #{cqlSort}
WHERE CQL_IDX = #{cqlIdx}


UPDATE EZ_CMS_QUICK_LINK 
SET CQL_SORT = #{changeSort}
WHERE USE_YN = 'Y'
AND CQL_USE = 'Y'
AND CQL_CODE = #{cqlCode}
AND CQL_SORT = #{cqlSort}


UPDATE EZ_CMS_QUICK_LINK 
SET CQL_SORT = CQL_SORT - 1
WHERE USE_YN = 'Y'
AND CQL_USE = 'Y'
AND CQL_CODE = #{cqlCode}
AND CQL_SORT > #{cqlSort}
