mapper.select:

SELECT ORD_NO
FROM   EZ_CMM_CODE
WHERE  CODE_UPR = #{codeUpr}
AND	   CODE     = #{code}

mapper.update:

UPDATE EZ_CMM_CODE
SET ORD_NO = ORD_NO + 1
WHERE ORD_NO >= #{ordNo}


UPDATE EZ_CMM_CODE
SET ORD_NO = ORD_NO - 1
WHERE ORD_NO > #{ordNo}


UPDATE EZ_CMM_CODE
SET  CODE_NAME  = #{codeName}
	,CODE_VALUE = #{codeValue}
WHERE  CODE_UPR = #{codeUpr}
AND	   CODE     = #{code}


DECLARE 
BEGIN
	UPDATE EZ_CMM_CODE
	SET ORD_NO = ORD_NO + 1
	WHERE ORD_NO = ( SELECT ORD_NO - 1 FROM EZ_CMM_CODE WHERE CODE = #{code} );

	UPDATE EZ_CMM_CODE
	SET ORD_NO = ORD_NO - 1
	WHERE CODE = #{code};
END;


 			DECLARE 
			BEGIN
				UPDATE EZ_CMM_CODE
				SET ORD_NO = ORD_NO - 1
				WHERE ORD_NO = ( SELECT ORD_NO + 1 FROM EZ_CMM_CODE WHERE CODE = #{code} );

				UPDATE EZ_CMM_CODE
				SET ORD_NO = ORD_NO + 1
				WHERE CODE = #{code};
			END;
