mapper.insert:

INSERT INTO EZ_MGR_AUTHORITY
(
	MA_MLIDX
	, MA_TYPE
	, MA_PKIDX
	, REG_ID
)
VALUES
(
	#{maMlidx}
	, #{maType}
	, #{maPkidx}
	, #{regId}
)


INSERT INTO EZ_MGR_AUTHORITY_HIS
	SELECT * 
	FROM EZ_MGR_AUTHORITY
	WHERE MA_MLIDX = #{roleIdx}
	AND MA_TYPE = #{searchType}

mapper.delete:

DELETE FROM EZ_MGR_AUTHORITY
WHERE MA_MLIDX = #{roleIdx}
AND MA_TYPE = #{searchType}

mapper.select:

SELECT
	count(*) as CNT
FROM  EZ_MGR_AUTHORITY
WHERE MA_MLIDX = #{roleIdx}



AND MA_TYPE = #{maType}




AND MA_PKIDX = #{maPkidx}
