mapper.select:
SELECT COUNT(*) FROM EZ_BBS_CONTENT A WHERE CB_IDX = '414' AND BC_DEL_YN = 'N' AND EXISTS ( SELECT 'X' FROM EZ_CONTENT_MINWON_RESULT CMR WHERE CMR.CB_IDX = A.CB_IDX AND CMR.BC_IDX = A.BC_IDX
AND CMR.MC_DEPT_CD = #{mcDeptCd}
)
AND REG_DT BETWEEN TO_DATE(#{scRegDtSt}, 'YYYY-MM-DD') AND TO_DATE(#{scRegDtEd}, 'YYYY-MM-DD') + 1

/* "selectMgrMainCommCount */
SELECT SUM(NUM) FROM( SELECT COUNT(*) AS NUM FROM EZ_RESERVATION_INDEX A WHERE RI_TYPE = 'F' AND USE_YN = 'Y'
AND REG_DT BETWEEN TO_DATE(#{scRegDtSt}, 'YYYY-MM-DD') AND TO_DATE(#{scRegDtEd}||'235900', 'YYYY-MM-DDHH24MISS')
UNION ALL SELECT COUNT(*) AS NUM FROM EZ_RESERVATION_INDEX A WHERE RI_TYPE = 'E' AND USE_YN = 'Y'
AND REG_DT BETWEEN TO_DATE(#{scRegDtSt}, 'YYYY-MM-DD') AND TO_DATE(#{scRegDtEd}||'235900', 'YYYY-MM-DDHH24MISS')
)
