/* insertUserHistory */
INSERT INTO EZ_CMS_USER_HIS (SEQ, CU_IDX, CU_ID, CU_PWD, EMAIL, ZIP, ADDR1, ADDR2, TEL_NUM, HP_NUM, CU_NAME, LAST_LOG_DT, IP, EMAIL_RCV_CHK, YOUNG_AGREE_NAME, PI_AGREE_DT, PI_AGREE_YN, SMS_RCV_YN, REG_DT, REG_ID, MOD_DT, MOD_ID, OWN_AUTH, PWD_CHG_DT, FID, MOD_REASON)
	SELECT (SELECT NVL(MAX(SEQ),0)+1 
					FROM EZ_CMS_USER_HIS), 
	A.CU_IDX, CU_ID, CU_PWD, EMAIL, ZIP, ADDR1, ADDR2, TEL_NUM, HP_NUM, CU_NAME, LAST_LOG_DT, IP, EMAIL_RCV_CHK, YOUNG_AGREE_NAME, PI_AGREE_DT, PI_AGREE_YN, SMS_RCV_YN, REG_DT, REG_ID, MOD_DT, MOD_ID, OWN_AUTH, PWD_CHG_DT, FID,
	#{reason}
FROM EZ_CMS_USER A
WHERE CU_ID = #{cuId}

mapper.update:
UPDATE EZ_CMS_USER SET EMAIL = #{email}, ZIP = #{zip}, ADDR1 = #{addr1}, ADDR2 = #{addr2}, TEL_NUM = #{telNum}, HP_NUM = #{hpNum}, EMAIL_RCV_CHK = #{emailRcvChk}, SMS_RCV_YN = #{smsRcvYn}, MOD_DT = to_char(sysdate,'yyyymmddhh24miss'), MOD_ID = #{modId}
,CU_PWD = #{cuPwd} ,PWD_CHG_DT = to_char(sysdate,'yyyymmddhh24miss')
WHERE CU_IDX = #{cuIdx}


UPDATE EZ_CMS_USER SET
	CU_PWD = #{cuPwd}
	,PWD_CHG_DT = to_char(sysdate,'yyyymmddhh24miss')
WHERE CU_IDX = #{cuIdx}

mapper.select:

SELECT * FROM EZ_CMS_USER
WHERE CU_ID = #{cuId}
AND CU_PWD = #{cuPwd}

mapper.update:

