mapper.insert:

INSERT INTO EZ_USER_COMMENT
(
	 SC_IDX
    ,MENU_URL
    ,REG_NAME
    ,PROFILE_URL
    ,REG_ID
    ,COMMENT_CONT
    ,REG_DT
)
VALUES
(
	#{scidx}
	,#{menuUrl}
	,#{nickName}
	,#{profileImage}
	,#{socialId}
	,#{commentCont}
	,to_char(sysdate,'yyyy-mm-dd hh24:mi')				
)

mapper.select:

SELECT * FROM (
	SELECT /*+ FIRST_ROWS */ A.*, ROWNUM RN FROM (
		SELECT * 
		FROM EZ_USER_COMMENT
		WHERE SC_IDX > ' ' 
		AND MENU_URL = #{menuUrl}


		ORDER BY REG_DT DESC,SC_IDX DESC
	) A WHERE ROWNUM <= #{lastIndex}
) WHERE RN > #{firstIndex}

mapper.update:

UPDATE EZ_CMS_USER
SET CU_PWD = #{cuPwd}
WHERE CU_ID = #{cuId}
AND P_HOMEPAGE = 'L'
