mapper.select:

SELECT * FROM (
	SELECT A.*, @rownum := @rownum + 1 AS RN FROM (
		SELECT *
		FROM EZ_CONTENT_MINWON_RESULT
		WHERE MC_STATUS IS NULL
		AND TEMP_YN IS NULL
		AND AUDIT_YN IS NOT NULL
		AND CB_IDX = #{cbIdx}
		AND BC_IDX = #{bcIdx}
		AND (@rownum:=0)=0 
		ORDER BY REG_DT DESC
	) A
) B WHERE RN = 1

mapper.update:

UPDATE EZ_CONTENT_MINWON_RESULT
SET
	MC_SURVEY = #{mcSurvey}
	, MC_SURVEY_DESC = #{mcSurveyDesc}
	, MC_SURVEY_REG_DT = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE CB_IDX = #{cbIdx}
AND BC_IDX = #{bcIdx}
AND MC_IDX = #{mcIdx}
