SELECT * FROM ( SELECT CE_IDX ,CE_DATE ,CE_UPDATE ,CE_SORT ,CE_NAME ,CE_MAILID ,CE_TEL ,CE_HP ,CE_USE ,CE_PHOTO ,(SELECT MAX(CD_SUBJECT) FROM EZ_DEPARTMENT WHERE CD_DEPSTEP1=A.CD_DEPSTEP1 AND CD_DEPSTEP2='00' AND CD_DEPSTEP3='00') as CE_DEPSTEP1 ,CASE WHEN A.CD_DEPSTEP2!='00' then (SELECT MAX(CD_SUBJECT) FROM EZ_DEPARTMENT WHERE CD_DEPSTEP1=A.CD_DEPSTEP1 AND CD_DEPSTEP2=A.CD_DEPSTEP2 AND CD_DEPSTEP3='00') end as CE_DEPSTEP2 ,CASE WHEN A.CD_DEPSTEP3!='00' then (SELECT MAX(CD_SUBJECT) FROM EZ_DEPARTMENT WHERE CD_DEPSTEP1=A.CD_DEPSTEP1 AND CD_DEPSTEP2=A.CD_DEPSTEP2 AND CD_DEPSTEP3=A.CD_DEPSTEP3) end as CE_DEPSTEP3 ,CASE WHEN FAKE_DEPT_ID IS NOT NULL AND CE_DEPSTEP4 IS NULL THEN '방문간호사' ELSE CE_DEPSTEP4 END as CE_DEPSTEP4 ,CE_WORKS ,CE_CDIDX ,CE_MEET ,CE_DUTY ,CD_DEPSTEP1 ,CD_DEPSTEP2 ,CD_DEPSTEP3 FROM EZ_EMPLOYEE, EZ_DEPARTMENT A WHERE ( CD_IDX=CE_CDIDX OR CD_IDX=FAKE_TEAM_CODE ) AND CE_USE = 'Y' AND (CE_SORT IS NULL OR CE_SORT != '99')
AND CD_DEPSTEP1 = #{cdDepstep1} AND CD_DEPSTEP2 = #{cdDepstep2} AND CD_DEPSTEP3 = #{cdDepstep3}
AND CD_DEPSTEP1 = #{cdDepstep1}
and CD_DEPSTEP2 = #{cdDepstep2}
and CD_DEPSTEP3 = #{cdDepstep3}
), EZ_EMPLOYEE_SORT WHERE CE_DEPSTEP4 = JIGWEE(+) ORDER BY CD_DEPSTEP1, CD_DEPSTEP2, CD_DEPSTEP3, CE_DEPSTEP2, CE_DEPSTEP3, SUNWEE, CE_SORT


SELECT CD_CODE
FROM EZ_DEPARTMENT
WHERE CD_IDX = #{cdIdx}
