SELECT * FROM ( SELECT /*+ FIRST_ROWS */ A.*, @rownum := @rownum + 1 as RN FROM ( SELECT * FROM(
SELECT PL_IDX , PL_CDIDX , PL_TITLE , PL_GUIDE , PL_AUTH_TYPE , PL_ADDR_YN , PL_TEL_YN , PL_HP_YN , PL_EMAIL_YN , PL_NUMBER , PL_RESULT_OPEN_YN , PL_MANAGER_NAME , PL_MANAGER_TEL , PL_START_DATE , PL_END_DATE , PL_USE , A.USE_YN , A.REG_DT , A.REG_ID , A.MOD_DT , A.MOD_ID , (SELECT CD_SUBJECT FROM EZ_DEPARTMENT WHERE CD_IDX = PL_CDIDX) AS CD_SUBJECT , 'N' PL_GUBUN FROM EZ_POLL_LIST A,EZ_POLL_USER B WHERE A.USE_YN = 'Y' AND A.PL_IDX = B.PU_PLIDX AND B.REG_DI = #{regDi} AND (PL_USE = 'N' OR PL_END_DATE < DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s')) UNION ALL SELECT PL_IDX , PL_CDIDX , PL_TITLE , PL_GUIDE , PL_AUTH_TYPE , PL_ADDR_YN , PL_TEL_YN , PL_HP_YN , PL_EMAIL_YN , PL_NUMBER , PL_RESULT_OPEN_YN , PL_MANAGER_NAME , PL_MANAGER_TEL , PL_START_DATE , PL_END_DATE , PL_USE , A.USE_YN , A.REG_DT , A.REG_ID , A.MOD_DT , A.MOD_ID , (SELECT CD_SUBJECT FROM EZ_DEPARTMENT WHERE CD_IDX = PL_CDIDX) AS CD_SUBJECT , 'Y' PL_GUBUN FROM EZ_POLL_LIST A,EZ_POLL_USER B WHERE A.USE_YN = 'Y' AND A.PL_IDX = B.PU_PLIDX AND B.REG_DI = #{regDi} AND PL_USE = 'Y' AND (PL_START_DATE <= DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') <= PL_END_DATE )
SELECT PL_IDX , PL_CDIDX , PL_TITLE , PL_GUIDE , PL_AUTH_TYPE , PL_ADDR_YN , PL_TEL_YN , PL_HP_YN , PL_EMAIL_YN , PL_NUMBER , PL_RESULT_OPEN_YN , PL_MANAGER_NAME , PL_MANAGER_TEL , PL_START_DATE , PL_END_DATE , PL_USE , A.USE_YN , A.REG_DT , A.REG_ID , A.MOD_DT , A.MOD_ID , (SELECT CD_SUBJECT FROM EZ_DEPARTMENT WHERE CD_IDX = PL_CDIDX) AS CD_SUBJECT , 'Y' PL_GUBUN FROM EZ_POLL_LIST A,EZ_POLL_USER B WHERE A.USE_YN = 'Y' AND A.PL_IDX = B.PU_PLIDX AND B.REG_DI = #{regDi} AND PL_USE = 'Y' AND (PL_START_DATE <= DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') <= PL_END_DATE )
SELECT PL_IDX , PL_CDIDX , PL_TITLE , PL_GUIDE , PL_AUTH_TYPE , PL_ADDR_YN , PL_TEL_YN , PL_HP_YN , PL_EMAIL_YN , PL_NUMBER , PL_RESULT_OPEN_YN , PL_MANAGER_NAME , PL_MANAGER_TEL , PL_START_DATE , PL_END_DATE , PL_USE , A.USE_YN , A.REG_DT , A.REG_ID , A.MOD_DT , A.MOD_ID , (SELECT CD_SUBJECT FROM EZ_DEPARTMENT WHERE CD_IDX = PL_CDIDX) AS CD_SUBJECT , 'N' PL_GUBUN FROM EZ_POLL_LIST A,EZ_POLL_USER B WHERE A.USE_YN = 'Y' AND A.PL_IDX = B.PU_PLIDX AND B.REG_DI = #{regDi} AND (PL_USE = 'N' OR PL_END_DATE < DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s'))
) B WHERE (@rownum:=0)=0 ORDER BY PL_IDX DESC ) A WHERE @rownum < #{lastIndex} ) C WHERE RN > #{firstIndex}

SELECT COUNT(*) FROM EZ_POLL_LIST A,EZ_POLL_USER B WHERE A.USE_YN = 'Y' AND A.PL_IDX = B.PU_PLIDX AND B.REG_DI = #{regDi}
AND PL_USE = 'Y' AND (PL_START_DATE <= DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') <= PL_END_DATE )
